                        </option>
                    </select>
                    <select>
                        <option value="LES ANIMES">
                            <Link to="/items/:cat">
                                <h2>DVD</h2>
                            </Link>
                        </option>
                    </select>
                </form>
                <Link to="/login">
                    <h2>CONNEXION</h2>
                </Link>
                <Link to="/cart">
                    <h2 className="cart">PANIER
                        <img src={cart} alt="icon-cart" /></h2>
                </Link>
            </nav>
        </div>
    );
};

export default Nav;
